mapper.select:
select cos_id, cos_category, cos_money, cos_date, cos_explain, cos_remarks, o.om_id, o.tom_name, o.tom_number, o.tom_card, u.uid, u.uname, b.bid, b.bname, f.fname as fjh,f1.fname as cshu,f2.fname as ldh from costflow c inner join oldman o on c.om_id = o.om_id inner join user u on c.uid = u.uid left join bed b on o.om_id = b.om_id left join floor f on b.fid = f.fid left join floor f1 on f1.fid = f.fdad left join floor f2 on f2.fid = f1.fdad
and o.tom_name like #{name}
and c.cos_category = #{lx}
and c.cos_date between #{kdate} and #{jdate}
order by cos_date desc
