mapper.select:
select o.out_id, o.om_id, o.use_uid, o.out_time, o.out_accompanyname, o.out_accompanyphone, o.out_returntime, o.out_returnestimate, o.out_outsum, o.out_reason, o.out_true, o2.om_id, o2.tsl_id, o2.ac_id, o2.fp_id, o2.tom_name, o2.tom_card, o2.tom_sex, o2.tom_number, o2.tom_address, o2.tom_category, o2.tom_diet, o2.tom_labour, o2.tom_check_intime, o2.tom_img from outregistration o left join oldman o2 on o.om_id = o2.om_id where 1=1
and o2.tom_name like concat('%',#{tomName,jdbcType=VARCHAR},'%')
and o.out_true=#{outtrue}
and o.out_time between #{time1} and #{time2}
order by o.out_id desc
